mapper.update:
UPDATE vacates
SET doctor_id = #{doctorId, jdbcType=VARCHAR}, description = #{description, jdbcType=VARCHAR},
    apply_date = #{applyDate, jdbcType=TIMESTAMP}, vacate_date = #{vacateDate, jdbcType=TIMESTAMP},
    status = #{status, jdbcType=INTEGER}, admin_id = #{adminId, jdbcType=VARCHAR}, admin_date = #{adminDate, jdbcType=TIMESTAMP}
WHERE vacate_id = #{vacateId, jdbcType=INTEGER}

mapper.select:
SELECT *
FROM vacates
WHERE vacate_id = #{vacateId, jdbcType=INTEGER}
